template.value:
/**
 * variable reference counter
 * <pre>{@code
 *
 *
 * example 1
 *
 *           		//automaticly close resource
 * 			VC_refcnt v_1 = new VC_refcnt( );
 * 			v_1.f_debug_incrementReferenceCounter();
 * 			v_obj=v_1;
 * 			v_1.f_debug_incrementReferenceCounter();
 * 			v_obj.f_debug_decrementReferenceCounter();
 * 			v_obj.f_debug_decrementReferenceCounter();
 *
 *
 *
 * example 2
 *
 *
 *
 *
 * }</pre>
 */
//      privateFinal public protectedPackage _package_ NOFINAL
private long c_referenceCounter = 0;




public void f_debug_incrementReferenceCounter( )
	{
	c_referenceCounter++;
	//log_.debug( "increment reference counter: " + c_referenceCounter );
	}




public void f_debug_decrementReferenceCounter( )
	{
	if( --c_referenceCounter > 0 )
		{
		return;
		}
	//log_.debug( "decrement reference counter: " + c_referenceCounter );
	}
