private MyImmutableEntryMap assertForNullProperties_( MyImmutableEntryMap object )
{
Objects.requireNonNull( object );
for( var i : object.entrySet() )
	{
	Objects.requireNonNull( i.getKey() );
	Objects.requireNonNull( i.getValue() );
	}

return object;
}


//<editor-fold desc=".">
/* Check properties of object for valid value



 */
//</editor-fold>



private MyImmutableEntryMap assertForValidProperties_( MyImmutableEntryMap object )
{
for( var i : object.entrySet() )
	{
	if( i.getValue() == "" )
		{
		throw new IllegalArgumentException(  i.toString() );
		}
	}



return object;

}


