//<editor-fold desc=".">
/* log if object is invalid and properties is null



 */
//</editor-fold>



private MyImmutableEntryMap logForNullProperties_( MyImmutableEntryMap object )
{
if( object == null )
	{
	log_.info( "Input object is null." );
	}

for( var i : object.entrySet() )
	{
	if( i.getKey() == null || i.getValue() == null )
		{
		log_.info( "Parameter is null:" + i);
		}
	}
return object;
}


