template.value:
//<editor-fold desc=".">
/*

- альтернатива Optional это аннотация @Nullabble
- запускает обработку на null значениях, классика nullable, те я потом должен буду проверить на null
- если вдруг переменная =null то get вернет иксепшен

.ifPresent( System.out::println );
.map( (z) -> { System.out.println( z ); return z; } 

 */
//</editor-fold>


Optional<$classname$> $var$ = Optional.ofNullable(new $classname$());

//var1
if ($var$.isPresent())
	System.out.println( $var$.get() );
//var2
var $var$_result = $var$.orElse("default value");

$END$

//<editor-fold desc=".">
/*

- запакуется значение переменной или дефолтное значение
- запускает продолжение обработки на дефолтных значениях, те я потом уже НЕ должен буду проверить на null

.ifPresent( System.out::println );
.map( (z) -> { System.out.println( z ); return z; } 

 */
//</editor-fold>

//var1
var $var$ = Optional<$classname$>.ofNullable($somevar$).orElse(new $classname$());
//var2
var $var$2 = Optional<$classname$>.ofNullable($somevar$).orElseGet(()->new $classname$());

var $var$_result = $var$.get();

$END$

public int readDuration(Properties props, String name) 
{
return Optional.ofNullable(props.getProperty(name)) .flatMap(OptionalUtility::stringToInt) .filter(i -> i > 0) .orElse(0); 
}

public Optional<String> $method$( final String string )
{
return Optional.ofNullable( string )
               .filter( StringUtils::isNotBlank )
               //.onEmpty( logAtWarningRunnable( "$class$.$method$(empty argument)" ) )
               .map( logAtDebugUnaryOperator( "$class$.$method$" ) )
               .map( CheckedFunction1.liftTry( this::$method$_ ) )
               .map( t -> t.onFailure( logAtErrorConsumer( "$class$.$method$" ) ) )
               .flatMap( t -> Optional.ofNullable( t.getOrNull() ) );
/**check postcondition and raise error*/
}



